		controls: true,
		nextText: 'Next',
		nextIcon: '<svg class="icon-slides icon-keyboard-arrow-right"><use xlink:href="#icon-keyboard-arrow-right"></use>',
		prevText: 'Prev',
		prevIcon:'<svg class="icon-slides icon-keyboard_arrow_left"><use xlink:href="#icon-keyboard_arrow_left"></use></svg>',
		nextSelector: null,
		prevSelector: null,
		autoControls: false,
		startText: 'Start',
		stopText: 'Stop',
		autoControlsCombine: false,
